#!/bin/bash
find build/release/bin -maxdepth 1 -type f -name '*' \! -name 'ryod' \! -name 'ryo-blockchain-export' \! -name 'ryo-wallet-cli' \! -name 'ryo-wallet-rpc' \! -name 'ryo-blockchain-import' \! -name 'ryo-blockchain-usage' \! -name 'ryo-address-validator' \! -name 'ryo-gen-trusted-multisig' \! -name 'ryo-blockchain-blackball' \! -name 'ryo-utils-deserialize' \! -name 'ryo-utils-object-sizes' \! -name '*.log' -exec mv -vn {} test-wallets \;
find build/debug/bin -maxdepth 1 -type f -name '*' \! -name 'ryod' \! -name 'ryo-blockchain-export' \! -name 'ryo-wallet-cli' \! -name 'ryo-wallet-rpc' \! -name 'ryo-blockchain-import' \! -name 'ryo-blockchain-usage' \! -name 'ryo-address-validator' \! -name 'ryo-gen-trusted-multisig' \! -name 'ryo-blockchain-blackball' \! -name 'ryo-utils-deserialize' \! -name 'ryo-utils-object-sizes' \! -name '*.log' -exec mv -vn {} test-wallets \;
